import React, { useEffect, useRef, useState } from "react";
import axios from 'axios';

const Form = () => {
  const titleInputRef = useRef();
  const submitButtonRef = useRef();
  const [taskTitle, setTaskTitle] = useState('');

  useEffect(() => {
    // This effect should only run when taskTitle changes.
    axios.post("http://localhost:5000/task", { title: taskTitle })
      .then(response => {
        console.log(response.data);
        setTaskTitle(''); // Clear the input field
      })
    .catch(error => {
        console.error("Erreur: ", error);
    });
}, [taskTitle]);

    const submitHandler = event => {
    event.preventDefault();
    // You can add further logic for form submission here if needed.
};

    const inputChangedHandler = () => {
    setTaskTitle(titleInputRef.current.value);
    };

    return (
    // Your form component
    <form onSubmit={submitHandler} className="form">
        <div>
        <label htmlFor="titleTask">Nom de la t√¢che</label>
        <input type="text" id="titleTask" ref={titleInputRef} onChange={inputChangedHandler} value={taskTitle} required />
        </div>
        <div>
        <button disabled={!taskTitle} ref={submitButtonRef}>
                Add Task
        </button>
        </div>
    </form>
    );
}

export default Form;
