import React, { useEffect, useRef, useState } from "react";
import axios from 'axios';

const Form = () => {
  const titleInputRef = useRef();
  const submitButtonRef = useRef();
  const [taskTitle, setTaskTitle] = useState('');

  const addTask=()=>{
    useEffect(() => {
    // This effect should only run when taskTitle changes.

        axios.post("http://localhost:4092/task", { title: taskTitle })
        .then(response => {
            console.log(response.data);
            setTaskTitle(''); 
        })
        .catch(error => {
            console.error("Erreur: ", error);
        });
    }, [taskTitle]);
}

    const submitHandler = event => {
    event.preventDefault();
    setTaskTitle(titleInputRef.current.value);
    
};

const inputChangedHandler = () => {
    submitButtonRef.current.disabled = !titleInputRef.current.value
    };

    return (
    
    <form action="#" onSubmit={submitHandler}>
        <div>
        <label htmlFor="titleTask">Name your Task</label>
        <input type="text" id="titleTask" ref={titleInputRef} onChange={inputChangedHandler}  required />
        </div>
        <div>
        <button disabled={!taskTitle} ref={submitButtonRef}>
                Add Task
        </button>
        </div>
    </form>
    );
}

export default Form;
